<?php/** * Template Name: Mag Service */get_header();?><div id="primary" class="about">    <div class="row">        <?php while (have_posts()) : the_post(); ?>            <div id="content" class="large-12 columns">                <?php the_content(); ?>            </div>         <?php endwhile; ?>    </div></div><!-- #primary --><section id="secondary-links">    <div class="row">        <div class="large-12 columns">            <h3>Check out other services</h3>        </div>        <?php        $theid = get_the_id();        $projects = get_pages(array('sort_column' => 'menu_order', 'sort_order' => 'ASC', 'child_of' => $theid));        if (!$projects) {            echo "problem";        } else {            foreach ($projects as $project) {                $permalink = get_permalink($project->ID);                ?>                <div class="service-section medium-4 columns">                    <div>                        <a class="greenbg" href="<?php echo $permalink; ?>"><?php echo get_the_post_thumbnail($project->ID, 'thumb'); ?></a>                        <h3><a title="Industrial Laundry Equipment" href="<?php echo $permalink; ?>"><?php echo $project->post_title; ?></a></h3>                    </div>                </div>                <?php            }        };        ?>    </div></section><?php get_footer(); ?>