<?php/** * Template Name: Mag Service Full Width Page */get_header();?><div id="primary" class="about">        <?php while (have_posts()) : the_post(); ?>        <div class="row">            <div id="content" class="large-12 columns">                <?php the_content(); ?>            </div>        </div>    <?php endwhile; ?></div><section id="secondary-links">    <div class="row">        <div class="large-12 columns">            <h3>Check out other services</h3>        </div>        <?php        $theid = $post->post_parent;        $projects = get_pages(array('sort_column' => 'menu_order', 'sort_order' => 'ASC', 'child_of' => $theid));        if (!$projects) {            echo "problem";        } else {            foreach ($projects as $project) {                $permalink = get_permalink($project->ID);                ?>                <div class="service-section medium-4 columns">                    <div>                        <a class="greenbg" href="<?php echo $permalink; ?>"><?php echo get_the_post_thumbnail($project->ID, 'thumb'); ?></a>                        <h3><a href="<?php echo $permalink; ?>"><?php echo $project->post_title; ?></a></h3>                    </div>                </div>                <?php            }        };        ?>    </div></section><?php get_footer(); ?>