<?php/** * Template Name: Mag Homepage */?><?php get_header(); ?><?php while (have_posts()) : the_post(); ?>    <div id="primary">        <div class="row">            <div class="large-12 columns">                <?php the_content(); ?>            </div>        </div>    </div><!-- #content --><?php endwhile; rewind_posts();?><?php$query_args = array(    'post_type' => 'product',    'posts_per_page' => 12,    'orderby' => 'rand',    'order'    => 'DESC');$products = new WP_Query($query_args);if ($products->have_posts()):    ?>    <div id="main-products">        <div class="row">            <div class="large-12 columns">                <h3>Laundry Equipment Range</h3>                <div class="product-slider">                    <?php while ($products->have_posts()) :                        $products->the_post();                        ?>                        <div class="product-item">                            <div class="product-quick-image"><a href="<?php the_permalink(); ?>" title="Primer <?php the_title();?>"><?php the_post_thumbnail('thumbnail'); ?></a></div>                            <p><a href="<?php the_permalink(); ?>" title="Primer <?php the_title();?>"><?php the_title(); ?></a></p>                            <p><a href="<?php the_permalink(); ?>" class="button" title="Primer <?php the_title();?>">View product</a></p>                        </div>    <?php endwhile; ?>                </div>            </div>        </div>    </div><?php endif; ?>    <?php wp_reset_postdata(); ?>    <div id="callout-container">        <div class="row">            <div class="large-12 columns"><?php dynamic_sidebar('widget-area-front-page'); ?>            </div>        </div>    </div><?php get_footer(); ?>