<?php/** * Single Post * * Learn more: http://codex.wordpress.org/Template_Hierarchy * * @package Jobify * @since Jobify 1.0 */get_header();?><?php while (have_posts()) : the_post(); ?>    <header class="page-header">        <div class="row">            <div class="large-12 columns">                <h1 class="page-title"><?php the_title(); ?></h1>                <div class="header-sub-text"><?php echo get_the_date(); ?></div>            </div>        </div>    </header>    <div id="primary">        <div class="row">            <div id="content" role="main">                <div class="blog-archive medium-8 columns">                    <article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>                        <?php if (is_singular() || has_post_thumbnail()) : ?>                            <div class="featured-image">                                <?php the_post_thumbnail('full'); ?>                            </div>                        <?php endif; ?>                        <div class="entry-summary">                            <?php if (is_singular()) : ?>                                <!--<div class="entry-feature">                                <?php //the_post_thumbnail( 'fullsize' ); ?>                        </div> -->                                <?php the_content(); ?>                                <?php if (is_singular()) : ?>                                    <?php the_tags('<p class="entry-tags"><i class="icon-tag"></i> ' . __('Tags:', 'jobify') . ' ', ', ', '</p>'); ?>                                    <?php wp_link_pages(array('before' => '<div class="page-links"><span class="page-links-title">' . __('Pages:', 'jobify') . '</span>', 'after' => '</div>', 'link_before' => '<span>', 'link_after' => '</span>')); ?>                                <?php endif; ?>                            <?php else : ?>                                <?php the_excerpt(); ?>                                <p><a href="<?php the_permalink(); ?>" rel="bookmark" class="button button-medium"><?php _e('Continue Reading', 'jobify'); ?></a></p>                            <?php endif; ?>                        </div>                        <div class="entry-meta">                            <?php get_template_part('parts/content', 'share'); ?>                        </div><!-- .entry-meta -->                    </article><!-- #post -->                    <?php comments_template(); ?>                </div>                <div id="blog-sidebar" class="medium-4 columns">                    <?php get_sidebar('widget-area-blog'); // sidebar 2;?>                        </div>            </div><!-- #content -->        </div>    </div><!-- #primary --><?php endwhile; ?><?php get_footer(); ?>